import java.util.Scanner;

public class Application {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("first number:");
		Scanner scanner = new Scanner(System.in);
		int firstNumber = scanner.nextInt();

		System.out.println("second number");
		int secondNumber = scanner.nextInt();

		// pseudo logic
		// is first number less than second number then 
		// minimum is first number
		
		
		
		
		
		
		
		
		
		if(firstNumber > secondNumber){
			System.out.println("The maximum number is " + firstNumber);
			System.out.println("The minimum number is " + secondNumber);
		} 
		else{
			System.out.println("The maximum number is " + secondNumber);
			System.out.println("The minimum number is " + firstNumber);
		}
	}
			
	}		
	
 



